param ( 
    [switch]$uninstall = $false, 
    [switch]$scan = $false, 
    [string]$folder_to_scan = "c:\") 
$macroav_version = 3.4.2

$macroav_file = ''

if (Test-Path -Path "c:\temp") {
    $Logfile = "c:\temp\macroav_deploy.log"
} 
else {
    New-Item -ItemType Directory -Force -Path "C:\temp"
    $Logfile = "c:\temp\macroav_deploy.log"
    Write-Output "c:\temp did not exist! Created it"
}

Function WriteLog {
    Param ([string]$LogString)
    $DateTime = "[{0:MM/dd/yy} {0:HH:mm:ss}]" -f (Get-Date)
    $LogMessage = "$Datetime $LogString"
    Add-content $LogFile -value $LogMessage
    Write-Output $LogMessage
}

if ($uninstall) {

    WriteLog "Uninstalling MacroAV"
    WriteLog "Stopping any running Excel processes" 
    Stop-Process -Force -Name excel -ErrorAction SilentlyContinue


    WriteLog "Checking currently enabled users"
    $local_users = Get-ChildItem -Directory c:\users -Name


    ForEach ($local_user in $local_users) {
        WriteLog "Found user $local_user, checking if MacroAV is installed for this user"

        $FilePath = "c:\users\$local_user\AppData\Roaming\Microsoft\Excel\XLSTART\macroav.xls"

        if (Test-Path -Path $FilePath) {
            Remove-Item -Path $FilePath -Force -ErrorAction SilentlyContinue
            if (Test-Path -Path $FilePath) {
                WriteLog "MacroAV under path $FilePath was not deleted, please remove it manually"
            }
            else {
                WriteLog "Deleted $FilePath" 
            }
        }

    }
    for ($i = 16 ; $i -ge 13 ; $i--) {
        WriteLog "Checking Default Installation path for Excel version $i "
        $FolderPath0 = "C:\Program Files\Microsoft Office\root\Office$i\XLSTART"
        $FolderPath1 = "C:\Program Files\Microsoft Office\Office$i\XLSTART"
        $FolderPath2 = "C:\Program Files (x86)\Microsoft Office\root\Office$i\XLSTART"
        $FolderPath3 = "C:\Program Files (x86)\Microsoft Office\Office$i\XLSTART"


        if (Test-Path -Path $FolderPath0) {
            Remove-Item -Path $FolderPath0\macroav.xlsm -Force -ErrorAction SilentlyContinue
            WriteLog "Deleted MacroAV in $FolderPath0\macroav.xlsm"
        }
        if (Test-Path -Path $FolderPath1) {
            Remove-Item -Path $FolderPath1\macroav.xlsm -Force -ErrorAction SilentlyContinue
            WriteLog "Deleted MacroAV in $FolderPath1\macroav.xlsm"
        }
        if (Test-Path -Path $FolderPath2) {
            Remove-Item -Path $FolderPath2\macroav.xlsm -Force -ErrorAction SilentlyContinue
            WriteLog "Deleted MacroAV in $FolderPath2\macroav.xlsm"
        }
        if (Test-Path -Path $FolderPath3) {
            Remove-Item -Path $FolderPath3\macroav.xlsm -Force -ErrorAction SilentlyContinue
            WriteLog "Deleted MacroAV in $FolderPath3\macroav.xlsm"
        }
    }
}
elseif ($scan) {
    Stop-Process -Name excel -ErrorAction SilentlyContinue 
    $files = Get-ChildItem $folder_to_scan -recurse | Where-Object { $_.extension -in ".xlsm", ".xls", ".xlm", ".xlsb", ".xltx", ".xltx", ".xltm", ".xlt", ".xlam", ".xla", ".xlw" } | ForEach-Object { $_.FullName }
    $num_of_files = 0
    ForEach ($file in $files) {
        WriteLog "scanning file $file" 
        if (Get-Content $file -Raw -Encoding ascii | Select-String -Pattern "todole|kangtang|KING|Office_|RESULTS|majoduck|kangatang") { 
            $excel = New-Object -comobject Excel.Application
            $excel.Visible = $false
            $excel.DisplayAlerts = $false
            $excel_path = $excel.Path 
            $wbPersonalXLSB = $excel.workbooks.open("$excel_path\XLSTART\macroav.xlsm")
            $workbook = $excel.Workbooks.Open($file)
            $workbook.CheckCompatibility = $False
            #access the Application object and run a macro 
            $app = $excel.Application
            $app.Run("macroav.xlsm!Scan_for_malware", "false")
            $wbPersonalXLSB.close()
            $workbook.save()
            $workbook.close()
            $excel.Quit()
            Stop-Process -Name excel -ErrorAction SilentlyContinue
            WriteLog "Executed MacroAV on $file"
        }

        $num_of_files++
    }
    Write-Output "Done! Scanned $num_of_files" 
}
else {
    WriteLog "Deploying MacroAV version $macroav_version"
    WriteLog "Checking currently enabled users"
    $local_users = Get-ChildItem -Directory c:\users -Name
    ForEach ($local_user in $local_users) {
        WriteLog "Found user $local_user"

        $FolderPath = "c:\users\$local_user\AppData\Roaming\Microsoft\Excel\XLSTART"
        if (Test-Path -Path $FolderPath) {
            WriteLog "Found Excel in path $FolderPath for $local_user , removing known persistence files for this user"

            Remove-Item -Path $FolderPath\macroav.xls -ErrorAction SilentlyContinue -Force
            Remove-Item -Path $FolderPath\k4.xls -ErrorAction SilentlyContinue -Force
            Remove-Item -Path $FolderPath\mypersonnel.xls -ErrorAction SilentlyContinue -Force
            Remove-Item -Path $FolderPath\RESULTS.xls -ErrorAction SilentlyContinue -Force
            Remove-Item -Path $FolderPath\KING.xls -ErrorAction SilentlyContinue -Force
            Remove-Item -Path $FolderPath\OFFICE_.XLS -ErrorAction SilentlyContinue -Force
        }
        else {
            WriteLog "Did not find XLSTART folder for user $local_user"
        }
    }

    for ($i = 16 ; $i -ge 13 ; $i--) {
        WriteLog "Checking Default Installation path for Excel version $i "
        $FolderPath0 = "C:\Program Files\Microsoft Office\root\Office$i\XLSTART"
        $FolderPath1 = "C:\Program Files\Microsoft Office\Office$i\XLSTART"
        $FolderPath2 = "C:\Program Files (x86)\Microsoft Office\root\Office$i\XLSTART"
        $FolderPath3 = "C:\Program Files (x86)\Microsoft Office\Office$i\XLSTART"


        if (Test-Path -Path $FolderPath0) {
            Remove-Item -Path $FolderPath0\macroav.xlsm -ErrorAction SilentlyContinue -Force
            $bytes = [Convert]::FromBase64String($macroav_file)
            [IO.File]::WriteAllBytes("$FolderPath0\macroav.xlsm", $bytes)
            WriteLog "Deployed MacroAV in $FolderPath0\macroav.xlsm"
        }
        if (Test-Path -Path $FolderPath1) {
            Remove-Item -Path $FolderPath1\macroav.xlsm -ErrorAction SilentlyContinue -Force
            $bytes = [Convert]::FromBase64String($macroav_file)
            [IO.File]::WriteAllBytes("$FolderPath1\macroav.xlsm", $bytes)
            WriteLog "Deployed MacroAV in $FolderPath1\macroav.xlsm"
        }
        if (Test-Path -Path $FolderPath2) {
            Remove-Item -Path $FolderPath2\macroav.xlsm -ErrorAction SilentlyContinue -Force
            $bytes = [Convert]::FromBase64String($macroav_file)
            [IO.File]::WriteAllBytes("$FolderPath2\macroav.xlsm", $bytes)
            WriteLog "Deployed MacroAV in $FolderPath2\macroav.xlsm"
        }
        if (Test-Path -Path $FolderPath3) {
            Remove-Item -Path $FolderPath3\macroav.xlsm -ErrorAction SilentlyContinue -Force
            $bytes = [Convert]::FromBase64String($macroav_file)
            [IO.File]::WriteAllBytes("$FolderPath3\macroav.xlsm", $bytes)
            WriteLog "Deployed MacroAV in $FolderPath3\macroav.xlsm"
        }
 

    }
}

WriteLog "DONE!"